// Initialize pipeline configuration
// Merge defaults with any incoming parameters

const defaults = {
  lead_count: 200,
  seed: null,
  enrichment_mode: 'offline',
  channels: ['email', 'linkedin'],
  generate_ab_variants: true,
  send_mode: 'dry_run',
  rate_limit: 10,
  batch_size: 50,
  max_retries: 3
};

// Get input from trigger (if any)
const input = $input.first()?.json || {};

// Merge with defaults
const config = { ...defaults, ...input };

// Add metadata
config.pipeline_id = `pipeline_${Date.now()}`;
config.started_at = new Date().toISOString();
config.mcp_server_url = $env.MCP_SERVER_URL || 'http://localhost:8000';

console.log('Pipeline Config:', JSON.stringify(config, null, 2));

return [{ json: config }];

// Validate generate_leads response
const input = $input.first().json;

// Check for HTTP errors
if (input.statusCode && input.statusCode !== 200) {
  throw new Error(`Generate leads failed: HTTP ${input.statusCode} - ${JSON.stringify(input.body)}`);
}

const response = input.body || input;

if (!response.success) {
  throw new Error(`Generate leads failed: ${response.message || 'Unknown error'}`);
}

// Validate lead count
if (!response.leads_generated || response.leads_generated === 0) {
  throw new Error('No leads were generated');
}

console.log(`‚úÖ Generated ${response.leads_generated} leads`);

return [{
  json: {
    step: 'generate_leads',
    success: true,
    leads_generated: response.leads_generated,
    message: response.message,
    timestamp: new Date().toISOString()
  }
}];

// Validate enrich_leads response
const input = $input.first().json;

// Check for HTTP errors
if (input.statusCode && input.statusCode !== 200) {
  throw new Error(`Enrich leads failed: HTTP ${input.statusCode} - ${JSON.stringify(input.body)}`);
}

const response = input.body || input;

if (!response.success) {
  throw new Error(`Enrich leads failed: ${response.message || 'Unknown error'}`);
}

console.log(`‚úÖ Enriched ${response.enriched_count} leads`);

return [{
  json: {
    step: 'enrich_leads',
    success: true,
    enriched_count: response.enriched_count,
    mode: response.mode,
    message: response.message,
    timestamp: new Date().toISOString()
  }
}];

// Validate generate_messages response
const input = $input.first().json;

// Check for HTTP errors
if (input.statusCode && input.statusCode !== 200) {
  throw new Error(`Generate messages failed: HTTP ${input.statusCode} - ${JSON.stringify(input.body)}`);
}

const response = input.body || input;

if (!response.success) {
  throw new Error(`Generate messages failed: ${response.message || 'Unknown error'}`);
}

console.log(`‚úÖ Generated ${response.messages_generated} messages`);

return [{
  json: {
    step: 'generate_messages',
    success: true,
    messages_generated: response.messages_generated,
    channels: response.channels,
    variants: response.variants_generated,
    message: response.message,
    timestamp: new Date().toISOString()
  }
}];

// Validate send_outreach response
const input = $input.first().json;

// Check for HTTP errors
if (input.statusCode && input.statusCode !== 200) {
  throw new Error(`Send outreach failed: HTTP ${input.statusCode} - ${JSON.stringify(input.body)}`);
}

const response = input.body || input;

if (!response.success) {
  throw new Error(`Send outreach failed: ${response.message || 'Unknown error'}`);
}

const successRate = response.sent_count / (response.sent_count + response.failed_count) * 100;

console.log(`‚úÖ Sent ${response.sent_count} messages (${response.failed_count} failed)`);

return [{
  json: {
    step: 'send_outreach',
    success: true,
    sent_count: response.sent_count,
    failed_count: response.failed_count,
    mode: response.mode,
    success_rate: successRate.toFixed(2),
    message: response.message,
    timestamp: new Date().toISOString()
  }
}];

// Compile final pipeline summary
const config = $('Initialize Config').first().json;
const status = $input.first().json;

// Get step results
const generateResult = $('Validate Generate').first()?.json || {};
const enrichResult = $('Validate Enrich').first()?.json || {};
const messagesResult = $('Validate Messages').first()?.json || {};
const sendResult = $('Validate Send').first()?.json || {};

const summary = {
  pipeline_id: config.pipeline_id,
  status: 'COMPLETED',
  started_at: config.started_at,
  completed_at: new Date().toISOString(),
  configuration: {
    lead_count: config.lead_count,
    enrichment_mode: config.enrichment_mode,
    channels: config.channels,
    send_mode: config.send_mode
  },
  results: {
    leads_generated: generateResult.leads_generated || 0,
    leads_enriched: enrichResult.enriched_count || 0,
    messages_generated: messagesResult.messages_generated || 0,
    messages_sent: sendResult.sent_count || 0,
    messages_failed: sendResult.failed_count || 0,
    success_rate: sendResult.success_rate || '0'
  },
  metrics: status.metrics || {},
  duration_seconds: Math.round((new Date() - new Date(config.started_at)) / 1000)
};

console.log('üéâ Pipeline completed successfully!');
console.log(JSON.stringify(summary, null, 2));

return [{ json: summary }];

// Error handler - captures any error and formats it
const config = $('Initialize Config').first()?.json || {};
const errorInfo = $input.first()?.json || {};

// Determine which step failed
let failedStep = 'unknown';
let errorMessage = 'Unknown error occurred';

try {
  // Check each validation node for errors
  const nodes = [
    { name: 'health_check', node: 'Server Healthy?' },
    { name: 'generate_leads', node: 'Generate OK?' },
    { name: 'enrich_leads', node: 'Enrich OK?' },
    { name: 'generate_messages', node: 'Messages OK?' },
    { name: 'send_outreach', node: 'Send OK?' }
  ];

  for (const step of nodes) {
    try {
      const result = $(step.node).first()?.json;
      if (result && result.success === false) {
        failedStep = step.name;
        errorMessage = result.message || result.error || 'Step failed';
        break;
      }
    } catch (e) {
      // Node not executed or error occurred
    }
  }

  // If we still don't know the error, check the error info
  if (failedStep === 'unknown' && errorInfo.error) {
    errorMessage = errorInfo.error;
  }
} catch (e) {
  errorMessage = e.message;
}

const errorReport = {
  pipeline_id: config.pipeline_id || 'unknown',
  status: 'FAILED',
  failed_at: new Date().toISOString(),
  started_at: config.started_at || null,
  error: {
    step: failedStep,
    message: errorMessage,
    details: errorInfo
  },
  configuration: {
    lead_count: config.lead_count,
    enrichment_mode: config.enrichment_mode,
    send_mode: config.send_mode
  },
  recovery_suggestions: [
    'Check MCP server logs: docker logs mcp-server',
    'Verify MCP server is running: curl http://localhost:8000/health',
    'Check network connectivity between n8n and MCP server',
    'Review the error message for specific issues',
    'Try reducing batch_size if timeout errors occur'
  ]
};

console.error('‚ùå Pipeline failed!');
console.error(JSON.stringify(errorReport, null, 2));

return [{ json: errorReport }];

// Server not healthy error
const config = $('Initialize Config').first()?.json || {};

return [{
  json: {
    pipeline_id: config.pipeline_id,
    status: 'FAILED',
    failed_at: new Date().toISOString(),
    error: {
      step: 'health_check',
      message: 'MCP Server is not healthy or not reachable',
      server_url: config.mcp_server_url
    },
    recovery_suggestions: [
      'Start the MCP server: docker-compose up -d mcp-server',
      'Check server logs: docker logs mcp-server',
      `Verify URL is correct: ${config.mcp_server_url}`,
      'Check firewall/network settings'
    ]
  }
}];
